mapper.select:
select hospital_num
from hospital
where hospital_id = #{hospitalId}

select *
from(
SELECT row_number() over (order by hospital_num) as rn
        ,     
from hospital


       hospital_name like '%' || #{searchWord} || '%'
      or hospital_category like '%' || #{searchWord} || '%'

    )                   
where rn between #{startRow} and #{endRow}
